import React, { Component } from 'react'
import CommentItem from '../CommentItem/CommentItem'
export default class CommentList extends Component {
    render() {
        return (
            <div>
                <div className='Comment-List'>
                    <CommentItem id='0' author='me' content='hello' />
                    <CommentItem id='1' author='jyeok' content='nice article!' />
                    <CommentItem id='2' author='choi' content='this is an epic comment!' />
                </div>

                <div className='Comment-Button'>
                    <textarea id='new-comment-content-input' placeholder='write comment here'/> <br/>
                    <button id='confirm-detail-article-button'>Submit</button> <br/>
                </div>
            </div>
        )
    }
}
