import React, { Component } from 'react'
import Logout from '../../Logout'
import {connect} from 'react-redux';
import Axios from 'axios';
class ArticleCreate extends Component {
    state = {
        title: '',
        content: '',
        edit: true,
    };

    onToggle = () => {
        this.setState({
            edit: !this.state.edit
        });

        if(edit) {
        }
    }

    onPost = () => {
        Axios.post('/articles'
        , {
            author_id: this.props.account.id,
            title: this.state.title,
            content: this.state.content
        })
        .then(this.props.history.push('/articles/'))
    }

    render() {
        return (
            <div className='ArticleCreate'>
                <div className='ArticleCreate-Common'>
                    <button id='write-tab-button' name='write-tab-button'>Write</button>
                    <button id='preview-tab-button' name='preview-tab-button'>Preview</button>
                    <button id='confirm-create-article-button' name='confirm-create-article-button' disabled={this.state.title === '' || this.state.content === ''} onClick={this.onPost}>Confirm</button>
                    <button id='back-create-article-button' name='back-create-article-button'>Back</button>
                </div>

                <div className='ArticleCreate-Write'>
                <h2> Article Write </h2>
                    <input type='text' id='article-title-input' name='article-title-input' placeholder='title here' onChange={(e) => {
                        this.setState({
                            title: e.target.value
                        })
                    }} /> <br/>
                    <textarea id='article-content-input' name='article-content-input' placeholder='content here' onChange={(e) => {
                        this.setState({
                            content: e.target.value
                        })
                    }}/> <br/>
                </div>

                <div className='ArticleCreate-Preview'>
                <h2> Article Preview</h2>
                    <h3 id='article-author'>author: choi</h3>
                    <h3 id='article-title'>title: lorem ipsum </h3>
                    <div id='article-content'>content: lorem ipsum de</div>
                </div>

                <Logout/>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        account: state.blog.account
    }
}

export default connect(mapStateToProps, null)(ArticleCreate);
