import * as actionTypes from '../action/actionTypes';

const initialState = {
    account: {
        email: '',
        id: -1,
        logged_in: false,
        name: '',
        password: ''
    },

    loginBehavior: null
}

const reducer = (state = initialState, action) => {
    switch(action.type) {
        case actionTypes.LOGIN_SUCCESS:
            state.loginBehavior = (() => action.behavior()),
            state.account = action.account;
            break;

        case actionTypes.LOGIN_FAILED:
            state.loginBehavior = (() => action.behavior());
            state.account = aciton.account;
            break;

        default:
            break;
    }
    console.log(state);
    return state;
}

export default reducer;
