import React, { Component } from 'react'
import {connect} from 'react-redux';
import * as actionCreators from '../../store/action/actionCreator';

class Logout extends Component {
    componentDidMount() {
        this.props.onLoad();
    }

    render() {
        return (
        <div>
            <div className="Logout">
                <br/><br/><button onClick={() => this.props.onLogout(this.state.account)}> {this.state.account.name}, Logout! </button>
            </div>
        </div>
        )
    }
}

const MapStateToProps = (state) => {
    return {
        account: state.blog.account
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        onLogout: (account) => {
            dispatch(actionCreators.LOGOUT(this.props.account));
        },

        onLoad: () => {
            dispatch(actionCreator.GET_LOGIN_INFO());
        }
    }
}

export default connect(MapStateToProps, mapDispatchToProps)(Logout);
