import React, { Component } from 'react'
import { connect } from 'react-redux';
import * as actionCreator from '../../store/action/actionCreator';
import axios from 'axios';

class Login extends Component {
    state = {
        account: {
            email: '',
            id: -1,
            logged_in: false,
            name: '',
            password: '',
        },
    }

    // componentDidMount() {
    //     axios.get('/api/user/1')
    //     .then((res) => console.log(res));
    // }

    render() {
        const loginHandler = () => {
            this.props.onLogin(this.state.account);
            if(this.state.account.logged_in) {
                console.log(this.state.account);
                alert('!!');
                this.props.history.push('/articles');        
            }
        }

        return (
             <div>
                <h1>Login</h1>
                <div className='LoginForm'>
                    Email: <input type="email" name="email-input" id="email-input" onChange={(e) => 
                    this.setState({
                        account: {
                            ...this.state.account,
                            email: e.target.value
                        }
                    })}/>
                    <br/>
                    Passowrd: <input type='password' name='pw-input' id='pw-input' onChange={(e) => this.setState({
                        account: {
                            ...this.state.account,
                            password: e.target.value
                        }
                    })}/>
                    <br/>
                    <button id="login-button" onClick={loginHandler}>Login</button>
                </div>
            </div>
        )
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        onLogin: (account) => {
            dispatch(actionCreator.LOGIN(account));
        }
    }
}

const mapStateToProps = (state) => {
    return {
        account: state.blog.account
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
