import React from 'react'
import CommentList from '../../Comment/CommentList'
import Logout from '../../Logout'
import {connect} from 'react-redux';
import Axios from 'axios';

const ArticleDetail = (props) => {
    let id = props.history.location.pathname.replace('/articles/', '')
    let articleInfo = {
        title: '',
        content: '',
        author: ''
    }
    Axios.get('/articles/' + id)
    .then((res) => {
        articleInfo.title = res.data.title
        articleInfo.content = res.data.content
        articleInfo.author = res.data.author_id
    });
    return (
        <div className='ArticleDetail'>
            <div className='ArticleContainer'>
                <h2> Article Detail</h2>
                <h3 id='article-author'>author: choi</h3>
                <h3 id='article-title'>title: lorem ipsum </h3>
                <div id='article-content'>content: lorem ipsum de</div>
            </div>

            <div className='ArticleDetail-Button'>
                <button id='edit-article-button'>Edit</button>
                <button id='delete-article-button'>Delete</button>
                <button id='back-detail-article-button'>Back</button>
            </div>
            <br/>
            <CommentList />
            <Logout history={props.history}/>
        </div>
    )
}


export default connect(mapDispatchToProps, mapStateToProps)(ArticleDetail);
