import React, { Component } from 'react'
import {connect} from 'react-redux';
import * as actionCreator from '../../store/action/actionCreator';

class Logout extends Component {
    componentDidMount() {
        this.props.fetchLoginInfo();
    }

    componentDidUpdate(prevProps, prevState) {
        console.log("aa");
        if(!this.props.account.logged_in) alert("go");
    }

    render() {
        const logoutHandler = () => {
            this.props.onLogout(this.props.account)
        }

        return (
        <div>
            <div className="Logout">
                <br/><br/><button onClick={logoutHandler}> {this.props.account.name}, Logout! </button>
            </div>
        </div>
        )
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        onLogout: (account) => {
            dispatch(actionCreator.LOGOUT(account))            
        },

        fetchLoginInfo: () => {
            dispatch(actionCreator.GET_LOGIN_INFO())
        }
    }
}

const mapStateToProps = (state) => {
    return {
        account: state.blog.account
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Logout);
