import * as actionTypes from '../action/actionTypes';

const initialState = {
    account: {
        email: '',
        id: -1,
        logged_in: false,
        name: '',
        password: ''
    },

    loginBehavior: null
}

const reducer = (state = initialState, action) => {
    switch(action.type) {
        case actionTypes.LOGIN_SUCCESS:
            const newState = {
                ...state,
                account: action.account,
                loginBehavior: action.behavior()
            };
            return newState;

        case actionTypes.LOGIN_FAILED:
            const failState = {
                ...state,
                account: action.account,
                loginBehavior: action.behavior()
            };
            return failState;

        case actionTypes.GET_LOGIN_INFO:
            // Todo
            return state;

        default:
            break;
    }
    return state;
}

export default reducer;
