import * as actionTypes from './actionTypes';
import axios from 'axios';

export const LOGIN_FAILED = (account) => {
    return {
        type: actionTypes.LOGIN_FAILED,
        account: account,
        behavior: () => alert("Email or Password is wrong")
    };
}

export const LOGIN_SUCCESS = (account) => {
    return {
        type: actionTypes.LOGIN_SUCCESS,
        account: account,
        behavior: () => axios.put('/api/user/1', {...account, logged_in : true}),
    }
}

export const LOGIN = (accountInformation) => {
    return (dispatch) => {
        return axios.get('/api/user/1')
        .then(
            (res) => {
                const validAccount = res.data;
                if(validAccount.email !== accountInformation.email || validAccount.password !== accountInformation.password) 
                    dispatch(LOGIN_FAILED(accountInformation));
                else {
                    console.log('validAccount is', validAccount);
                    dispatch(LOGIN_SUCCESS(validAccount));
                }
            }
        );
    }
}

export const LOGOUT = (accountInformation) => {
    return (dispatch) => {
    }
}
