import React, { Component } from 'react'
import Logout from '../../Logout'
import Axios from 'axios'
export default class ArticleEdit extends Component {
    state= {
        title: '',
        content: '',
        edit: true
    }

    onPost = () => {
        const id = this.props.history.location.pathname.repliace('/articles/edit/', '');

        Axios.patch('/articles', {
            author_id: this.props.account.id,
            title: this.state.title,
            content: this.state.content
        })
        .then(this.props.history.push('/articles/'))
    }
    render() {
        return (
            <div className='ArticleEdit'>
                <div className='ArticleEdit-Common'>
                    <button id='write-tab-button' name='write-tab-button'>Write</button>
                    <button id='preview-tab-button' name='preview-tab-button'>Preview</button>
                    <button id='confirm-create-article-button' name='confirm-create-article-button'>Confirm</button>
                    <button id='back-create-article-button' name='back-create-article-button'>Back</button>
                </div>

                <div className='ArticleEdit-Write'>
                <h2> Article Write </h2>
                    <input type='text' id='article-title-input' name='article-title-input' placeholder='title here'/> <br/>
                    <textarea id='article-content-input' name='article-content-input' placeholder='content here'/> <br/>
                </div>

                <div className='ArticleEdit-Preview'>
                <h2> Article Preview</h2>
                    <h3 id='article-author'>author: choi</h3>
                    <h3 id='article-title'>title: lorem ipsum </h3>
                    <div id='article-content'>content: lorem ipsum de</div>
                </div>

                <Logout/>
            </div>
        )
    }
}
