import React, {Component} from 'react'
import CommentList from '../../Comment/CommentList'
import Logout from '../../Logout'
import Axios from 'axios';

class ArticleDetail extends Component { 
    id = this.props.history.location.pathname.replace('/articles/', '')
    state = {
        articleInfo : {
        title: '',
        content: '',
        author: ''
        },

        username: ''
    };

    constructor(props) {
        super(props);
        Axios.get('/articles/' + this.id)
        .then((res) => {this.setState({
            articleInfo: {
                title : res.data.title,
                content : res.data.content,
                author : res.data.author_id
            }
        })});
        Axios.get('/user/' + this.id)
        .then((res) => {
            this.setState({
                username: res.data[this.state.articleInfo.author - 1]
            })
        })
    }

    render() { 
    return (
        <div className='ArticleDetail'>
            <div className='ArticleContainer'>
                <h2> Article Detail</h2>
                <h3 id='article-author'>{this.state.articleInfo.author}</h3>
                <h3 id='article-title'>{this.state.articleInfo.title}</h3>
                <div id='article-content'>{this.state.articleInfo.content}</div>
            </div>

            <div className='ArticleDetail-Button'>
                <button id='edit-article-button'>Edit</button>
                <button id='delete-article-button'>Delete</button>
                <button id='back-detail-article-button'>Back</button>
            </div>
            <br/>
            <CommentList />
            <Logout history={this.props.history}/>
        </div>
    )
}
}


export default (ArticleDetail);
