import React, { Component } from 'react'

export default class CommentItem extends Component {
    render() {
        return (
            <div className='CommentItem'>
                <b>{this.props.author}</b> <span className='Comment-content'>{this.props.content}</span>
                <button id='edit-comment-button'>Edit</button> <button id='delete-comment-button'>Delete</button>
            </div>
        )
    }
}
