import React, {Component} from 'react'
import {Link} from 'react-router-dom';
import Logout from '../../Logout';
import {connect} from 'react-redux';
import * as actionCreator from '../../../store/action/actionCreator';

class ArticleList extends Component {
    state = {
        articleList: []
    }

    componentDidMount() {
        this.props.getAllArticles();
    }

    static getDerivedStateFromProps(nextProps, prevState) {    
        let tableTemplate = <table border="1">
            <th>id</th>
            <th>title</th>
            <th>author</th>
        </table>;

        let articles = nextProps.articles.map((article) => {
            return(
                <tr>
                    <td>{article.id}</td>
                    <td>{article.title}</td>
                    <td>{article.author_id}</td>
                </tr>
            );
        });

        this.setState({
            ...state,
            articleList: articles
        })
    }


    render() {
        return (  
            <div className="ArticleList">
                <ul className="AritleListContainer">
                    <li> id:0 post one author:kim </li>
                    <li> id:1 post two author:park</li>
                    <li> id:2 post three author:jim</li>
                </ul>

                <button className="ArticleCreate" id="create-article-button" name="create-article-button">Create</button> <br/>
                <Link to='/login'>Login Page</Link>
                <Logout history={this.props.history} />
            </div>
        );
    }
}

    const mapStateToProps = (state) => {
        return {
            articles: state.blog.articleList
        }
    }

    const mapDispatchToProps = (dispatch) => {
        return {
            getAllArticles: () => {
                dispatch(actionCreator.GET_ALL_ARTICLES());
            }
        }
    }

export default connect(mapStateToProps, mapDispatchToProps)(ArticleList);
