import * as actionTypes from './actionTypes';
import axios from 'axios';

export const LOGIN_ = (accountInformation) => {
    return {
        type: actionTypes.LOGIN,
        email: accountInformation.email,
        password: accountInformation.password
    };
}

export const LOGIN = (accountInformation) => {
    axios.get('/api/user/1')
    .then(
        (res) => {
            const validAccount = res.data;
            if(validAccount.email !== accountInformation.email || validAccount.password !== accountInformation.password) alert("Email or Password is wrong");
        }
    );
}
