import React, {Component} from 'react'
import {Link} from 'react-router-dom';
import Logout from '../../Logout';
import axios from 'axios';
import {connect} from 'react-redux';
import * as actionCreator from '../../../store/action/actionCreator'
class ArticleList extends Component {
    state = {
        articleShow : [],
        articles: []
    }

    componentDidMount() {
        axios.get('/api/articles')
        .then((res) => {
            this.articles = res.data.map((article) => {
                return {
                    articleID: article.id, 
                    title: article.title,
                    author: article.author_id
                }
            });
        })
    }

    clickHandler = (id) => {
        this.props.history.push('/articles/'+id);
    }

    render() {
        return (  
            <div className="ArticleList">
                {this.state.articleShow}
                <button className="ArticleCreate" id="create-article-button" name="create-article-button" onClick={() => this.props.history.push('/articles/create')}>Create</button> <br/>
                <Link to='/login'>Login Page</Link>
                <Logout history={this.props.history} />
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        userList: state.blog.users
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        getUserList: () => {
            dispatch(actionCreator.GET_USER_LIST);
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ArticleList);
